import React, { useState } from 'react';
import { Button, Modal, ModalHeader, ModalBody, ModalFooter } from 'reactstrap';
import { Alert, Form, Row, Col, FormGroup, Label, Input } from 'reactstrap';
import { useToasts } from 'react-toast-notifications'
import {CopyToClipboard} from 'react-copy-to-clipboard';
import Icon from './Icon';
import { faTwitter, faFacebook, faWhatsapp, faInstagram, faLinkedin } from '@fortawesome/free-brands-svg-icons'

const shareOptions = [
    {
        name: 'Facebook',
        icon: faFacebook ,
        link: 'http://facebook.com/#',
    },
    {
        name: 'Twitter',
        icon: faTwitter,
        link: '#',
    },
    {
        name: 'WhatsApp',
        icon: faWhatsapp,
        link: '#',
    },
    {
        name: 'Instagram',
        icon: faInstagram,
        link: '#',
    },
    {
        name: 'LinkedIn',
        icon: faLinkedin,
        link: '#',
    },
]

const JoinModal = (props) => {
  const {
    buttonLabel,
    className
  } = props;

  const [modal, setModal] = useState(false);

  const toggle = () => setModal(!modal);
  const { addToast } = useToasts()

  const handleCopy = () => {
    setModal(false);
    addToast('Link copied successfully!', {
        appearance: 'success',
        autoDismiss: true,
      })
  }

  return (
    <div>
        <Button block onClick={toggle}>{buttonLabel}</Button>
      <Modal isOpen={modal} toggle={toggle} className={className}>
        <ModalHeader toggle={toggle}>
            <h3>Join this Activity</h3>
            <p>Your name will be used by your host to identify you and We'll use your email address to send you details of the activity</p>
        </ModalHeader>
        <ModalBody>
            <Form>
                <Row form>
                    <Col md={6}>
                    <FormGroup>
                        <Label for="exampleEmail">Email</Label>
                        <Input type="email" name="email" id="exampleEmail" placeholder="with a placeholder" />
                    </FormGroup>
                    </Col>
                    <Col md={6}>
                    <FormGroup>
                        <Label for="examplePassword">Password</Label>
                        <Input type="password" name="password" id="examplePassword" placeholder="password placeholder" />
                    </FormGroup>
                    </Col>
                </Row>     
            </Form>  

            <hr/>
            <h4>Copy Link</h4>
            <Form inline >
                <FormGroup style={{width: '80%'}} >
                    <Input type="text" style={{width: '100%'}}  name="password" id="link" placeholder="http://hoplist.com/activity/123456" disabled/>
                </FormGroup>
                {' '}
                {/* <Toast title="Copy" content="Link copied to clipboard"/> */}
                <CopyToClipboard text={"http://hoplist.co/activity/123456"} onCopy={handleCopy}>
                <Button color="dark">
                    Copy
                </Button>
                </CopyToClipboard>
            </Form>

            <hr/>


        </ModalBody>
        <ModalFooter>
            <Alert color="danger">
                <h4 style={{fontWeight: 'normal'}}>Sharing Tip</h4>
                <p>You can copy and paste the link anywhere (Slack, LinkedIn, Snapchat, Telegram etc.)</p>
            </Alert>

        </ModalFooter>
      </Modal>
    </div>
  );
}

export default JoinModal;
