import React, { useState } from 'react';
import { Button, Modal, ModalHeader, ModalBody, ModalFooter } from 'reactstrap';
import { Alert, Form, FormGroup, Label, Input } from 'reactstrap';
import { useToasts } from 'react-toast-notifications'
import {CopyToClipboard} from 'react-copy-to-clipboard';

const shareOptions = [
    {
        name: 'Facebook',
        icon: '',
        link: '',
    },
    {
        name: 'Facebook',
        icon: '',
        link: '',
    },
    {
        name: 'Facebook',
        icon: '',
        link: '',
    },
]

const ShareModal = (props) => {
  const {
    buttonLabel,
    className
  } = props;

  const [modal, setModal] = useState(false);

  const toggle = () => setModal(!modal);
  const { addToast } = useToasts()

  const handleCopy = () => {
    setModal(false);
    addToast('Link copied successfully!', {
        appearance: 'success',
        autoDismiss: true,
      })
  }

  return (
    <div>
      <Button color="default" onClick={toggle}>{buttonLabel}</Button>
      <Modal isOpen={modal} toggle={toggle} className={className}>
        <ModalHeader toggle={toggle}>
            <h3>Share this Activity</h3>
        </ModalHeader>
        <ModalBody>
            {
                shareOptions.map((option, index) => 
                    <div key={index}>
                        {option.name}
                    </div>
                )
            }

            <hr/>
            <Form inline>
                <FormGroup>
                    <Label for="link" hidden>Password</Label>
                    <Input type="text" name="password" id="link" placeholder="Password" disabled/>
                </FormGroup>
                {' '}
                {/* <Toast title="Copy" content="Link copied to clipboard"/> */}
                <CopyToClipboard text={"http://hoplist.co/activity/1234456"} onCopy={handleCopy}>
                <Button className="btn btn-success">
                    Copy
                </Button>
                </CopyToClipboard>
            </Form>

            <hr/>


        </ModalBody>
        <ModalFooter>
            <Alert color="warning">
                <h3>Sharing Tips</h3>
                <p>You can copy and paste the link anywhere (Slack, LinkedIn, Snapchat, Telegram etc.)</p>
            </Alert>

        </ModalFooter>
      </Modal>
    </div>
  );
}

export default ShareModal;
