import React, { useState } from 'react';
import { Button, Modal, ModalHeader, ModalBody, ModalFooter } from 'reactstrap';
import { Alert, Form, Row, Col, FormGroup, Label, Input } from 'reactstrap';
import { useToasts } from 'react-toast-notifications'
import {CopyToClipboard} from 'react-copy-to-clipboard';
import Icon from './Icon';
import { faTwitter, faFacebook, faWhatsapp, faInstagram, faLinkedin } from '@fortawesome/free-brands-svg-icons'

const shareOptions = [
    {
        name: 'Facebook',
        icon: faFacebook ,
        link: '#',
    },
    {
        name: 'Twitter',
        icon: faTwitter,
        link: '#',
    },
    {
        name: 'WhatsApp',
        icon: faWhatsapp,
        link: '#',
    },
    {
        name: 'Instagram',
        icon: faInstagram,
        link: '#',
    },
    {
        name: 'LinkedIn',
        icon: faLinkedin,
        link: '#',
    },
]

const ShareModal = (props) => {
  const {
    buttonLabel,
    className
  } = props;

  const [modal, setModal] = useState(false);

  const toggle = () => setModal(!modal);
  const { addToast } = useToasts()

  const handleCopy = () => {
    setModal(false);
    addToast('Link copied successfully!', {
        appearance: 'success',
        autoDismiss: true,
      })
  }

  return (
    <div>
      <Button color="light" onClick={toggle}>{buttonLabel}</Button>
      <Modal isOpen={modal} toggle={toggle} className={className}>
        <ModalHeader toggle={toggle}>
            <h3>Share this Activity</h3>
        </ModalHeader>
        <ModalBody>
            <div style={{textAlign: 'center', display:'flex'}}>
            <Row>

            {
                shareOptions.map((option, index) => 
                    <Col md={2} key={index}>
                        <a class="social-share">
                            <div class="social-share-icon">
                                <div class="icon-container">
                                    <Icon icon={option.icon} fixedWidth={false} />                                    
                                </div>
                            </div>
                         {option.name}
                        </a>
                    </Col>
                )
            }
            </Row>
            </div>
            <hr/>
            <Form inline>
                <FormGroup>
                    <Label for="link" hidden>Password</Label>
                    <Input type="text" name="password" id="link" placeholder="Password" disabled/>
                </FormGroup>
                {' '}
                {/* <Toast title="Copy" content="Link copied to clipboard"/> */}
                <CopyToClipboard text={"http://hoplist.co/activity/1234456"} onCopy={handleCopy}>
                <Button className="btn btn-success">
                    Copy
                </Button>
                </CopyToClipboard>
            </Form>

            <hr/>


        </ModalBody>
        <ModalFooter>
            <Alert color="warning">
                <h3 style={{fontWeight: 'normal'}}>Sharing Tip</h3>
                <p>You can copy and paste the link anywhere (Slack, LinkedIn, Snapchat, Telegram etc.)</p>
            </Alert>

        </ModalFooter>
      </Modal>
    </div>
  );
}

export default ShareModal;
